    public partial class frmHidratacaoCorporal : Form
    {
        public frmHidratacaoCorporal()
        {
            InitializeComponent();
        }

        private void pictureSkinCream_Click(object sender, EventArgs e)
        {
            MessageBox.Show("Skin Cream Ureia 3%\r\nCreme hidratante para o corpo\r\n\r\nIndicações:\r\n• Tratamento das peles secas e ressecadas\r\n• Hidratação Corporal\r\n\r\nBenefícios:\r\n• Hidratação com toque seco\r\n• Excelente textura aveludada\r\n• Formulação de fácil absorção\r\n\r\nModo de usar:\r\nAplicar uma fina camada do produto duas a três vezes ao dia no corpo, após o banho. Se necessário aplicar o produto várias vezes ao dia.\r\n\r\nApresentação:\r\nFrasco contendo 350ml\r\n\r\nDetalhes:\r\n\r\nO Skin Cream foi especialmente desenvolvido para atuar de forma severa e duradoura nas peles mais ressecadas. A partir de uma base que contém em sua fórmula Ureia e óleo de amêndoas, proporcionando um efeito sensorial agradável e sedoso, auxiliando a pele a encontrar seu equilíbrio natural. A tecnologia Dermafine faz com que as substâncias presentes no produto, adquiram a mesma estrutura da barreira protetora da pele, resultando na perfeita e completa reposição dos óleos naturais, penetrando rapidamente na pele e transportando os óleos vegetais garantindo uma hidratação intensa e compensando a perda de umidade, o que resulta em maior maciez, aumento da elasticidade, diminuição da aspereza e aumento da hidratação.", "Skin Cream Ureia 3%");
        }

        private void pictureLinhaSaphis_Click(object sender, EventArgs e)
        {
            MessageBox.Show("Extrem Derme Corpo 240ml\r\nCreme para o Corpo\r\n\r\nIndicações:\r\n• Estrias\r\n• Telangiectasias\r\n• Microvarizes\r\n• Redução do Vermelhidão\r\n• Coadjuvante na drenagem linfática\r\n• Hidratação Corporal\r\n\r\nBenefícios:\r\n• Hidratação corporal estimulando os colágenos 1, 3, 4 e 7\r\n• Agradável fragrância\r\n• Toque seco e aveludado\r\n\r\nModo de usar:\r\nAplicar no corpo diariamente quantas vezes forem necessárias.\r\n\r\nApresentação:\r\nFrasco contendo 240ml\r\n\r\nDetalhes:\r\nO cuidado com a pele exige um tratamento além da hidratação. Com base nesta necessidade desenvolvemos Extrem Derme. Extrem Derme possui em sua fórmula a Dermochlorella 1%, que é um extrato da microalga verde Chlorella Vulgaris, rica em oligopeptídeos e aminoácidos. A Dermochlorella 1% atua na reestruturação da pele com ação no colágeno I e III e por estas ações específicas a Dermochlorella 1% oferece um tratamento que vai além da hidratação, atuando como firmador da pele com indicações amplas.", "Extrem Derme Corpo 240ml");
        }
